import React, { useContext, useEffect, useState } from 'react';
import { Form, Modal, Button, Container } from 'react-bootstrap';
import TextEditor from '../admin/TextEditor';
import useInputState from '../../hooks/useInputState';
import { ProjectsActions } from '../../context/contexts/projects.context';

const NewProjects = (props) => {
	const [ project, handleChange, handleDesc, reset, fileChange ] = useInputState({});
	const { addProject } = useContext(ProjectsActions);
	const [file, setFile] = useContext('');

	const handleFile = (e) => {
		setFile({file: e.target.files[0]});
	}

	const handleSubmit = (e) => {
		e.preventDefault();
		const form_data = new FormData(newP);
		// form_data.set('title', project.title);
		// form_data.set('code', project.code);
		// form_data.set('demo', project.demo);
		// form_data.set('description', project.description);
		// form_data.append('projectImg', project.projectImg);
		console.log(form_data);
		addProject(form_data);
	};

	return (
		<Modal size="lg" show={props.show} onHide={props.toggle} centered style={{zIndex: "9999"}}>
			<Modal.Header closeButton>
				<Modal.Title id="example-modal-sizes-title-lg">New Portfolio Project</Modal.Title>
			</Modal.Header>
			<Modal.Body>
				<Container>
					<Form id='newP' onSubmit={handleSubmit}>
						<Form.Group>
							<Form.Control
								name="title"
								value={project.title}
								onChange={handleChange}
								type="text"
								placeholder="Title"
							/>
						</Form.Group>

						<Form.Group>
							<Form.Control
								name="code"
								value={project.code}
								onChange={handleChange}
								type="text"
								placeholder="Source Link"
							/>
						</Form.Group>

						<Form.Group>
							<Form.Control
								name="demo"
								value={project.demo}
								onChange={handleChange}
								type="text"
								placeholder="Demo Link"
							/>
						</Form.Group>

						<Form.Group>
							<TextEditor value={project.description} onChange={handleDesc} />
						</Form.Group>

						
						<input type="file" name="projectImg" onChange={fileChange} />
						

						<Button variant="primary" type="submit">
							Submit
						</Button>
					</Form>
				</Container>
			</Modal.Body>
		</Modal>
	);
};

export default NewProjects;
