import React from 'react';
import { Modal, Form, Button } from 'react-bootstrap';
import TextEditor from '../admin/TextEditor';
import useInputState from '../../hooks/useInputState';

const NewProjects = (props) => {
	const [ project, handleChange, handleDesc, reset ] = useInputState('');

	return (
		<Modal size="lg" show={props.show} onHide={props.toggle} aria-labelledby="example-modal-sizes-title-lg">
			<Modal.Header closeButton>
				<Modal.Title id="example-modal-sizes-title-lg">Large Modal</Modal.Title>
			</Modal.Header>
			<Modal.Body>
				<Form>
					<Form.Group controlId="formBasicEmail">
						<Form.Label>Email address</Form.Label>
						<Form.Control type="email" placeholder="Enter email" />
						<Form.Text className="text-muted">We'll never share your email with anyone else.</Form.Text>
					</Form.Group>

					<Form.Group controlId="formBasicPassword">
						<Form.Label>Password</Form.Label>
						<Form.Control type="password" placeholder="Password" />
					</Form.Group>
					<Form.Group controlId="formBasicCheckbox">
						<Form.Check type="checkbox" label="Check me out" />
					</Form.Group>
					<TextEditor value={project.description} onChange={handleDesc} />
					<Button variant="primary" type="submit">
						Submit
					</Button>
				</Form>
			</Modal.Body>
		</Modal>
	);
};

export default NewProjects;
