import React, { useContext, useEffect } from 'react';
import { Container } from 'react-bootstrap';
import Router from 'next/router';

import { AuthActions, AuthContext } from '../../context/contexts/auth.context';
import Login from '../../components/admin/Login';

import styles from '../../styles/pages/login_page.module.scss';

const index = () => {
	const { checkAuth } = useContext(AuthActions);
	const { isAuthenticated } = useContext(AuthContext);

	isAuthenticated && Router.push('/admin/dashboard');

	useEffect(() => {
		checkAuth();
		console.log('hhfdhdf');
	}, []);

	return (
		<div>
			<div className={styles.sidebar}>
				<div className={styles.sidebar__header}>
					<svg className={styles.sidebar__svg} xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1440 320">
						<path
							fill="#f3f4f5"
							fill-opacity="1"
							d="M0,96L48,112C96,128,192,160,288,154.7C384,149,480,107,576,80C672,53,768,43,864,48C960,53,1056,75,1152,85.3C1248,96,1344,96,1392,96L1440,96L1440,0L1392,0C1344,0,1248,0,1152,0C1056,0,960,0,864,0C768,0,672,0,576,0C480,0,384,0,288,0C192,0,96,0,48,0L0,0Z"
						/>
					</svg>
					<h6 className={styles.sidebar__title}>Dashboard Login</h6>
				</div>
				<p className={styles.sidebar__footer}>Copyright &copy; Dominic Cattell 2019</p>
			</div>
			<div className={styles.login_page}>
				<Login />
			</div>
		</div>
	);
};

export default index;

