import React from 'react';
import { Container, Row, Col, Form, Button } from 'react-bootstrap';

import useInputState from '../../hooks/useInputState';

import styles from '../../styles/login.module.scss';

const index = () => {
	const [user, handleChange] = useInputState("");

	return (
		<Container className={styles.login__form}>
			<Form>
				<Form.Group controlId="formBasicEmail">
					<Form.Label>Username</Form.Label>
					<Form.Control type="email" placeholder="Enter email" value={user.username} onChange={handleChange}/>
					<Form.Text className="text-muted" />
				</Form.Group>

				<Form.Group controlId="formBasicPassword">
					<Form.Label>Password</Form.Label>
					<Form.Control type="password" placeholder="Password" value={user.password} onChange={handleChange}/>
				</Form.Group>
				<Button variant="primary" type="submit">
					Login
				</Button>
			</Form>
		</Container>
	);
};

export default index;
