import React, { useState } from 'react';
import { Modal } from 'react-bootstrap';
//only load Quill on client side. depends on document, if not present on SSR then will not load
const ReactQuill = typeof window === 'object' ? require('react-quill') : () => false;

const NewProjects = (props) => {
	const [ value, setValue ] = useState('');

	return (
		<Modal size="lg" show={props.show} onHide={props.toggle} aria-labelledby="example-modal-sizes-title-lg">
			<Modal.Header closeButton>
				<Modal.Title id="example-modal-sizes-title-lg">Large Modal</Modal.Title>
			</Modal.Header>
			<Modal.Body>
				<ReactQuill theme="snow" value={value} onChange={setValue} />
			</Modal.Body>
		</Modal>
	);
};

export default NewProjects;
