import React, { useContext } from 'react';
import { Container, Row, Col, Form, Button, Alert } from 'react-bootstrap';

import useInputState from '../../hooks/useInputState';
import useToggle from '../../hooks/useToggle';

import { AuthActions, AuthContext } from '../../context/contexts/auth.context';

import styles from '../../styles/components/login.module.scss';

const Login = () => {
	const [ user, handleChange ] = useInputState('');
	const [ alert, showAlert ] = useToggle(true);

	const { authMsgs } = useContext(AuthContext);
	const { loginUser } = useContext(AuthActions);

	const handleSubmit = async (e) => {
		e.preventDefault();
		await loginUser(user);
		!alert && showAlert();
	};

	return (
		<Container className={styles.login__form}>
			<Form onSubmit={handleSubmit}>
				<Form.Group controlId="formBasicEmail">
					<Form.Label>Username</Form.Label>
					<Form.Control
						className={styles.default_input}
						type="text"
						name="username"
						value={user.username || ''}
						onChange={handleChange}
					/>
					<Form.Text className="text-muted" />
				</Form.Group>

				<Form.Group controlId="formBasicPassword">
					<Form.Label>Password</Form.Label>
					<Form.Control type="password" name="password" value={user.password || ''} onChange={handleChange} />
				</Form.Group>
				<Button variant="primary" type="submit">
					Login
				</Button>
			</Form>
		</Container>
	);
};

export default Login;
