import React, { useContext, useEffect } from 'react';
import { Container } from 'react-bootstrap';
import Router from 'next/router';

import { AuthActions, AuthContext } from '../../context/contexts/auth.context';
import Login from '../../components/admin/Login';

import styles from '../../styles/pages/login_page.module.scss';

const index = () => {
	const { checkAuth } = useContext(AuthActions);
	const { isAuthenticated } = useContext(AuthContext);

	isAuthenticated && Router.push('/admin/dashboard');

	useEffect(() => {
		checkAuth();
	}, []);

	return (
		<div>
			<div className={styles.sidebar}>
				<h6 className={styles.sidebar__header}>
					Dominic Cattell <span className={styles.sidebar__header__sub}>CMS</span>
				</h6>
				<p className={styles.sidebar__footer}>Copyright &copy; Dominic Cattell 2019</p>
			</div>
			<div className={styles.login_page}>
				<Login />
			</div>
		</div>
	);
};

export default index;
