import React, { useContext, useEffect } from 'react';
import { Container, Row, Col, Form, Button, Navbar, Nav } from 'react-bootstrap';
import Router from 'next/router';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

import styles from '../../styles/pages/dashboard_page.module.scss';
import AdminNavbar from '../../components/admin/AdminNavbar';
import AdminProject from '../../components/admin/AdminProject';

import { ProjectsContext, ProjectsActions } from '../../context/contexts/projects.context';
import WithAuth from '../../HOC/auth.hoc';

const dashboard = () => {
	const { getProjects } = useContext(ProjectsActions);
	const { projects } = useContext(ProjectsContext);

	useEffect(() => {
		getProjects();
	}, []);

	return (
		<div className={styles.dashboard}>
			<div className={styles.dashboard__sidebar}>
				<h5 className={styles.dashboard__sidebar__header}>Pages</h5>
				<ul className={styles.dashboard__sidebar__list}>
					<li><FontAwesomeIcon className={styles.dashboard__sidebar__icon} icon="home"/><span className={styles.dashboard__sidebar__text} >Home</span></li>
					<li><FontAwesomeIcon className={styles.dashboard__sidebar__icon} icon="at"/><span className={styles.dashboard__sidebar__text}>Contact</span></li>
					<li><FontAwesomeIcon className={styles.dashboard__sidebar__icon}  icon="smile"/><span className={styles.dashboard__sidebar__text}>About</span></li>
				</ul>
				<button className={styles.dashboard__projects__btn}>New Project</button>

			</div>
			<div className={styles.dashboard__projects}>
				<div className={styles.dashboard__projects__header}>
					<h5 className={styles.dashboard__projects__title}>Projects</h5>
				</div>
				{projects.map((project) => (
					<AdminProject key={project._id} title={project.title} img={project.projectImg} />
				))}
			</div>

			<AdminNavbar />
		</div>
	);
};

export default WithAuth(dashboard);
